CREATE FUNCTION [dbo].[checkpasswd](@login nvarchar(50), @passwd nvarchar(255))
RETURNS int
AS
BEGIN
	DECLARE @salt nvarchar(50) = N'Самая соль!';
	DECLARE @user_id int;

	SELECT @user_id = u.id
	FROM dbo.users u
	WHERE lower(u.login) = LTRIM(RTRIM(lower(@login)))
		AND u.password = LOWER(CONVERT(Varchar(255), HASHBYTES('SHA2_256', @salt+@passwd), 2))

	RETURN @user_id
END


