CREATE TRIGGER [dbo].[userIU]
   ON  [dbo].[users]
   AFTER INSERT,UPDATE
AS 
BEGIN
	SET NOCOUNT ON;
	IF TRIGGER_NESTLEVEL() > 1 RETURN
	
	DECLARE @salt nvarchar(50) = N'Самая соль!';
	
	UPDATE u SET u.[password] = LOWER(CONVERT(Varchar(255), HASHBYTES('SHA2_256', @salt+i.password), 2))
	FROM dbo.users u
	INNER JOIN INSERTED i ON i.id = u.id
	LEFT JOIN DELETED d ON d.id = u.id AND d.password = i.password
	WHERE d.id IS NULL
END
GO
